local link = function (_, parent1, parent2, property1, property2) local metatbl = getmetatable(parent1)  metatbl.__index = function (self, key) if key == property1 then return parent2[property2]   end return rawget(parent1, key)   end metatbl.__newindex = function (self, key, value) if key == property1 then parent2[property2]  = value  end rawset(parent1, key, value)  end setmetatable(parent1, metatbl)  end local linker = { link = link }  return linker 